///     var simpleDrag: some Gesture {
///         DragGesture()
///             .onChanged { value in
///                 self.location = value.location
///                self.isDragging = true
///
///             }
///             .onEnded{_ in self.isDragging = false}
///     }
///
///     var body: some View {
///         Circle()
///             .fill(self.isDragging ? Color.red : Color.yellow)
