///
///     var body: some View {
///           VStack {
///               Picker(selection: $selectedFruit, label: Text("Select Favorite Fruit")) {
///                   ForEach(0..<fruits.count) {
///                       Text(self.fruits[$0])
///                   }
///               }
///
///               Text("Your Favorite Fruit: \(self.fruits[selectedFruit])")
///           }
///           .pickerStyle(MenuPickerStyle())
///     }
/// }
/// ```
